con:config.script:
import com.eviware.soapui.impl.wsdl.WsdlTestSuite
import com.eviware.soapui.support.XmlHolder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext
//log.info "My first Program"

//log.info context.expand('${#TestCase#dept}')

testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Add"].getPropertyValue("Response")

def testcase = testRunner.testCase.testSuite.testCases["EmployeeData"]

def name = testcase.getPropertyValue("name")
def id = testcase.getPropertyValue("id")
def dept = testcase.getPropertyValue("dept")
def age = testcase.getPropertyValue("age")

def req = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Add"].getPropertyValue("Request")

def xmlAdd =  new XmlHolder(req)

xmlAdd.setNodeValue("//typ:addEmployee/typ:name",name)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept) 
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)

def newAddXml = xmlAdd.getXml();

testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Add"].setPropertyValue("Request",newAddXml)

def AddTeststep = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Add"]

def runAddContext = new WsdlTestRunContext(AddTeststep)

AddTeststep.run(testRunner,runAddContext)


//GetEmployeeRequestRun


def reqGet = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Get"].getPropertyValue("Request")

def xmlGet =  new XmlHolder(reqGet)

xmlGet.setNodeValue("//typ:getEmployeeDetails/typ:employeeName",name)

def newGetXml = xmlGet.getXml();

testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Get"].setPropertyValue("Request",newGetXml)

def GetTeststep = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Get"]

def runGetContext = new WsdlTestRunContext(AddTeststep)

GetTeststep.run(testRunner,runGetContext)


//DeleteEmployeeRequestRun

def resGet = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Get"].getPropertyValue("Response")

def xmlGetRes = new XmlHolder(resGet)

def nameRes = xmlGetRes.getNodeValue("//ns:name")

log.info nameRes

def reqDel = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Delete"].getPropertyValue("Request")

def xmlDel =  new XmlHolder(reqDel)

xmlDel.setNodeValue("//typ:deleteEmployee/typ:employeeName",nameRes)

def newDelXml = xmlGet.getXml();

testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Get"].setPropertyValue("Request",newDelXml)

def DelTeststep = testRunner.testCase.testSuite.testCases["EmployeeData"].testSteps["Delete"]

def runDelContext = new WsdlTestRunContext(AddTeststep)

DelTeststep.run(testRunner,runDelContext)
